#!/bin/bashnnecho "=== USER/GROUP COMMAND DIAGNOSTIC ==="necho ""nnecho "1. Checking for adduser/addgroup commands:"nwhich adduser 2>/dev/null || echo "adduser: NOT FOUND"nwhich addgroup 2>/dev/null || echo "addgroup: NOT FOUND"necho ""nnecho "2. Checking for useradd/groupadd commands:"nwhich useradd 2>/dev/null || echo "useradd: NOT FOUND"nwhich groupadd 2>/dev/null || echo "groupadd: NOT FOUND"necho ""nnecho "3. Checking for other user management commands:"nwhich usermod 2>/dev/null || echo "usermod: NOT FOUND"nwhich groupmod 2>/dev/null || echo "groupmod: NOT FOUND"nwhich userdel 2>/dev/null || echo "userdel: NOT FOUND"nwhich groupdel 2>/dev/null || echo "groupdel: NOT FOUND"necho ""nnecho "4. Checking what's in /usr/sbin:"nls -la /usr/sbin/add* /usr/sbin/user* /usr/sbin/group* 2>/dev/null || echo "No user/group commands in /usr/sbin"necho ""nnecho "5. Checking what's in /usr/bin:"nls -la /usr/bin/add* /usr/bin/user* /usr/bin/group* 2>/dev/null || echo "No user/group commands in /usr/bin"necho ""nnecho "6. Checking what packages are installed:"ndpkg -l | grep -E "(adduser|passwd|shadow|util-linux)" || echo "No relevant packages found"necho ""nnecho "7. Checking if we can install packages:"nif command -v apt-get >/dev/null 2>&1; thenn    echo "apt-get is available"n    echo "Trying to install adduser package..."n    apt-get updaten    apt-get install -y addusern    echo "After installation attempt:"n    which adduser 2>/dev/null || echo "adduser still not found"n    which addgroup 2>/dev/null || echo "addgroup still not found"nelsen    echo "apt-get is NOT available"nfinecho ""nnecho "8. Checking PATH:"necho $PATHnecho ""nnecho "9. Checking if we can manually create group:"necho "Trying to create group manually..."nif command -v addgroup >/dev/null 2>&1; thenn    addgroup kiosk 2>&1nelif command -v groupadd >/dev/null 2>&1; thenn    groupadd kiosk 2>&1nelsen    echo "No group creation commands available"nfinecho ""nnecho "=== END DIAGNOSTIC ==="