#!/bin/bashnnecho "=== MANUAL GROUP CREATION ==="nn# Check if running as rootnif [ "$EUID" -ne 0 ]; thenn    echo "ERROR: This script must be run as root (use sudo)"n    exit 1nfinnecho "1. Installing essential packages..."napt-get updatenapt-get install -y adduser passwd shadow util-linuxnnecho ""necho "2. Checking what commands are now available..."nwhich addgroup 2>/dev/null && echo "✓ addgroup found" || echo "✗ addgroup not found"nwhich groupadd 2>/dev/null && echo "✓ groupadd found" || echo "✗ groupadd not found"nwhich adduser 2>/dev/null && echo "✓ adduser found" || echo "✗ adduser not found"nwhich useradd 2>/dev/null && echo "✓ useradd found" || echo "✗ useradd not found"nnecho ""necho "3. Trying to create group using available commands..."nn# Try addgroupnif command -v addgroup >/dev/null 2>&1; thenn    echo "Trying addgroup..."n    if addgroup kiosk 2>&1; thenn        echo "✓ Group created with addgroup"n        exit 0n    elsen        echo "✗ addgroup failed"n    finfinn# Try groupaddnif command -v groupadd >/dev/null 2>&1; thenn    echo "Trying groupadd..."n    if groupadd kiosk 2>&1; thenn        echo "✓ Group created with groupadd"n        exit 0n    elsen        echo "✗ groupadd failed"n    finfinn# Try manual group creation by editing /etc/groupnecho "Trying manual group creation by editing /etc/group..."nif ! grep -q "^kiosk:" /etc/group; thenn    echo "kiosk:x:1001:" >> /etc/groupn    echo "✓ Group added manually to /etc/group"nelsen    echo "✓ Group already exists in /etc/group"nfinnecho ""necho "4. Verifying group creation..."nif getent group kiosk >/dev/null 2>&1; thenn    echo "✓ Group kiosk exists and is accessible"n    getent group kiosknelsen    echo "✗ Group kiosk does not exist"nfinnecho ""necho "=== END MANUAL GROUP CREATION ==="